css:
/*pc*/@media (min-width: 601px){

.header__nav--full {padding-top: 0px;}
  .top-bar {background-color: #ffffff;}
  .header__nav .header__category {color: #222222;}

}


 /*mobile*/@media (max-width: 600px) {
.top-bar{display:none;}

}

 /*responsivo*/
.main{background-color: #fff;}
.info-bar__item{color: #000000;}
.info-bar__item{font-family: 'Poppins';}
.info-bar__title {
    font-family: 'Poppins';
    font-size: 18px;
    font-style: normal;
    font-weight: 700;
    margin-left: 0;
}
.info-bar {
    background-color: #ffffff;
    box-shadow: none;
}
.products-carousel__title{font-size:23px;font-family:'Poppins', sans-serif;font-weight: 800;color:#3a9e92;}
.page-title__head h1 {font-family: 'Poppins'; font-weight: 800;
color:#3a9e92;  line-height: 32px;}
.header{box-shadow:-6px 15px 14px -9px rgba(0,0,0,0.37);}
.header__nav--full:before {background: #fff;}
.header__nav--full {padding-top: 3px;}
.header__toggler-icon{color: #000000;}
.top-bar__nav a {color: #2a2a2a;font-size: 15px;}
.i-user{color:#000; font-size:20px;}
.i-shopping-bag{color:#000; font-size:20px;}
#cart-count{background-color:#3b988d;}
.header__search-btn {font-size: 15px;}
#header {background-color: #ffffff;border-color: #ffffff;}
.share-network-whatsapp{display:none;}
.share-network-facebook{display:none;}
.share-network-twitter{display:none;}
.share-network-telegram{display:none;}
.share-network-pinterest{display:none;}
.share-network-email{display:none;}
.share-network-sms {display:none;}
#product-payment-header{display:none;}
.d-lg-inline {display: none!important;}
.product__short-stock{display: none;}
.menu__list a {background: transparent;font-size: 16px;
font-weight:500;font-family:'Poppins';text-decoration: none;}
.menu__list a:hover {background: transparent;font-size: 16px;
font-weight:800;font-family:'Poppins';text-decoration: none;}
.menu__item{font-size: 16px;font-weight: 800;
font-family:'Poppins'; border-bottom:none;}
.menu__item a:hover { background: transparent;font-size:16px;
  font-weight:800;font-family: 'Poppins';text-decoration: none;
  border-bottom:none;}
.menu__phone{font-family: 'Poppins';font-size: 14px;}
.menu__link{font-size: 14px;font-family: 'Poppins';}
.header__nav .header__category {color: #080808;font-size: 16px;}
.footer {background-color: #6e6e6e;border-color: #6e6e6e;color: #ffffff;
}
.footer__credits { border-top: var(--border-width) solid transparent;}
#go-to-top {background-color: #3a9e92;border-color: #3a9e92;color: #ffffff;
}
.btn-primary{background-color:background-color: #e3e3e3;
border-color: #e3e3e3;color: #0000;}
.btn-primary:hover{background-color: #efefef;border-color: #e1e1e1;
color: #020202;}
.i-shopping-bag {color:#e15822;font-size: 24px;
}
.product-card__link {color: #0a0a0a;font-family: 'Poppins'; font-size:15px;}
h4{font-family: 'Poppins'; font-weight: 800;color:#3a9e92;}
h1 {font-family: 'Poppins'; font-weight: 800;
   color:#3a9e92; }
.page-title__head{font-family: 'Poppins'; font-weight: 800;
   color:#3a9e92; }
.footer__contacts a {
    color: #ffffff;
    font-size: 15px;
}
}
